using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Threading.Tasks;
using DotNetEnv;
using Newtonsoft.Json;
using CommunityToolkit.WinUI.Notifications; // For toast notifications
using Windows.UI.Notifications;             // For the actual 'ToastNotification' class

class Program
{
    static async Task Main(string[] args)
    {         
        // Load environment variables from .env file
        Env.Load("./.env");

        // Read values
        string apiKey = Environment.GetEnvironmentVariable("API_KEY");
        string apiUrl = Environment.GetEnvironmentVariable("API_URL");

         // Use them in your app
        Console.WriteLine($"API Key: {apiKey}");
        Console.WriteLine($"API URL: {apiUrl}");



        if (string.IsNullOrEmpty(apiKey) || string.IsNullOrEmpty(apiUrl))
        {
            Console.WriteLine("Missing API_KEY or API_URL in .env file!");
        }
        else
        {
            Console.WriteLine("Environment variables loaded successfully!");
        }

       
         double prob = await GetProbabilityAsync(apiUrl, apiKey);
         string message = GetMessageFromProbability(prob);
         Console.WriteLine($"{prob}");

         ShowToastNotification(message);

         Console.WriteLine($"Toast sent with message: '{message}' (prob={prob:F2}). Press any key to exit.");
     }


