        string jsonPayload = JsonConvert.SerializeObject(data);
        var content = new StringContent(jsonPayload);
        content.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        HttpResponseMessage response = await client.PostAsync(url, content);

        if (!response.IsSuccessStatusCode)
        {
            Console.WriteLine($"Request failed: {response.StatusCode}");
            return 0;
        }

        string result = await response.Content.ReadAsStringAsync();
        Console.WriteLine($"Raw API result: {result}");

        // Parse JSON array [0.75] -> 0.75
        try
        {
            var arr = JsonConvert.DeserializeObject<double[]>(result);
            if (arr != null && arr.Length > 0)
            {
                return arr[0];
            }
            else
            {
                return 0;
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error parsing JSON: {ex.Message}");
            return 0;
        }
    }

    static string GetMessageFromProbability(double prob)
    {
        if (prob < 0.25)
            return "Connection is stable.";
        else if (prob >= 0.25 && prob < 0.5)
            return "Your connection is degrading, please take action.";
        else if (prob >= 0.5 && prob < 0.75)
            return "Your connection is degrading further, please take action soon.";
        else
            return "Your connection is degrading beyond usability.";
    }

    static void ShowToastNotification(string message)
    {
        // Create toast content using the builder
        var toastContent = new ToastContentBuilder()
            .AddText("Network Alert")
            .AddText($"{message}")
            .AddButton(new ToastButton().SetContent("Fix Now").AddArgument("action", "fixNow"))
            .AddButton(new ToastButton().SetContent("Send to Admin").AddArgument("action", "sendToAdmin"))
            .GetToastContent();

        // Create a ToastNotification object from the content XML
        var toast = new ToastNotification(toastContent.GetXml());

        // Show the toast (make sure your app ID is correct)
        ToastNotificationManagerCompat.CreateToastNotifier().Show(toast);

        Console.WriteLine("Toast with two buttons sent. Press any key to exit.");
    }
